Scripts.OnClick:
if self.click == nil or self.click == 0 then
GHI_ShowTextForMarking(GHI_CurrentBookID,GHI_CurrentBookPage);
self.click = 1;

self:SetText("View Text");
elseif self.click == 1 then
GHI_ItemTextFrame:Show();
GHI_ItemTextPageText:Show();
GHI_ItemTextPageMarkable:Hide();
self:SetText("Mark Text");

self.click = 0;
end

Scripts.OnUpdate:
local n = tonumber(self:GetText());
if n then
if n > GHI_MAX_FONT_SIZE then
self:SetText(GHI_MAX_FONT_SIZE);
end
end

local n = tonumber(self:GetText());
if n then
if n > GHI_MAX_FONT_SIZE then
self:SetText(GHI_MAX_FONT_SIZE);
end
end

OnEnterPressed.OnUpdate:
local n = tonumber(self:GetText());
if n then
if n > GHI_MAX_FONT_SIZE then
self:SetText(GHI_MAX_FONT_SIZE);
end
end

Scripts.OnClick:
GHI_BookSafePage();
GHI_ItemTextFrameCurrentPage:SetText(GHI_EditPage.."/"..GHI_CurrentBook.pages);
GHI_CurrentBookPage = GHI_EditPage;
if GHI_CurrentBook.pages == 1 then
GHI_ItemTextFramePrevPageButton:Hide();
GHI_ItemTextFrameNextPageButton:Hide();
else
GHI_ItemTextFramePrevPageButton:Show();
GHI_ItemTextFrameNextPageButton:Show();
end

GHI_ShowBook(GHI_CurrentBook.title,GHI_CurrentBook[GHI_EditPage],GHI_CurrentBookEdit,GHI_CurrentMaterial,GHI_CurrentBook.font,GHI_CurrentBook.n,GHI_CurrentBook.h1,GHI_CurrentBook.h2);

Scripts.OnHide:
--Close$parent();
GHI_BookSafePage();
PlaySound("igMainMenuClose");
GHI_EditPage = nil;
GHI_EditID = nil;

Scripts.OnValueChanged:
local num = tonumber(self:GetValue());
if not num then num = 10 end;
local parent = self:GetParent():GetName();
local TL = _G[parent.."LogoTopLeft"];
local TR = _G[parent.."LogoTopRight"];
local BL = _G[parent.."LogoBottomLeft"];
local BR = _G[parent.."LogoBottomRight"];

TL:SetTexture("Textures\\GuildEmblems\\Emblem_"..num.."_16_TU_U");
TR:SetTexture("Textures\\GuildEmblems\\Emblem_"..num.."_16_TU_U");
BL:SetTexture("Textures\\GuildEmblems\\Emblem_"..num.."_16_TL_U");
BR:SetTexture("Textures\\GuildEmblems\\Emblem_"..num.."_16_TL_U");

_G[self:GetName().."Label2"]:SetText((num));
